import React, { useState } from "react";
import axios from "axios";
import "./App.css";
// import { Button, InputField } from "./components/atoms";
import { BrowserRouter as Router, Route, useNavigate } from 'react-router-dom';
import Navbar from "./pages/Navbar";

import {
  Home,
  LoginPage,
  AfterLogin,
} from './pages';

const apiUrl = "http://localhost:3001";

axios.interceptors.request.use(
  (config) => {
    const { origin } = new URL(config.url);

    const allowedOrigins = [apiUrl];

    const token = localStorage.getItem("token");

    if (allowedOrigins.includes(origin)) {
      config.headers.authorization = `Bearer ${token}`;
    }

    return config;
  },

  (error) => {
    return Promise.reject(error);
  }
);

function App(){
  const storedJwt = localStorage.getItem("token");
  const [jwt, setJwt] = useState(storedJwt || null);
  const [foods, setFoods] = useState([]);
  const [fetchError, setFetchError] = useState(null);

  const initialvalue = {
    username: "",
    password: "",
  }

  const [loginData, setLoginData] = useState(initialvalue);

  //POST
  const getJwt = async (value) => {
    const postData = {
      username: value.username, //input from form field username
      password: value.password, //input from form field password
    };

    const { data } = await axios.post(`${apiUrl}/jwt`, postData);

    localStorage.setItem("token" , data.token);

    setJwt(data.token);
  };

  //GET
  const getFoods = async () => {
    try {
      const { data } = await axios.get(`${apiUrl}/foods`);

      setFoods(data);

      setFetchError(null);
    } 
    catch(err) {
      setFetchError(err.message);
    }
  };

  const handleChange = (e) => {
    e.persist();
    setLoginData(() => ({
      [e.target.name]: e.target.value,
    }));
  };

  // function handleSubmit(e) {
  //   e.preventDefault();
  // }

  return (
    <Router>

      <div className="App">
        <Navbar />
        <div className="content">
          <useNavigate>
            <Route exact path="/">
              <Home />
            </Route>
            <Route path="/home">
              <Home />
            </Route>
            <Route path="/login">
              <LoginPage />
            </Route>
            <Route path="/loggedIn">
              <AfterLogin />
            </Route>
          </useNavigate>
        </div>
      </div>
      {/* This section is only shown if the user is */}

      <section style={{ marginBottom: "10px" }}>

<section>
      {/* Convert this into a form */}
      <form>

        <input
        name="username"
        label="USERNAME"
        value={loginData.username}
        onChange={handleChange} 
        />

        <input
        name="password"
        label="PASSWORD"
        value={loginData.password}
        onChange={handleChange}
        />

      </form>
</section>
      {/* Also try to show some error message if login fails */}

      <button onClick={() => getJwt(loginData)}> LOGIN </button>

      {/* Also remove this and show some message instead */}
      {jwt && (
        <pre>
          <code>{jwt}</code>
        </pre>
      )}

      </section>

      <section>

        {/* This section should only be displayed if logged in */}
          <button onClick={() => getFoods()}>Get Foods</button>

          <ul>
            {foods.map((food, i) => (
                <li>{food.description}</li>
            ))}
          </ul>

          {fetchError && <p style = {{ color: "red"}}>{fetchError}</p>}
      </section>
    </Router>
  );
}

export default App;
